import React, {Component} from 'react';


class App extends Component{
  state = {
    isCitizen : false
  };

  onChange = e =>{
    this.setState({isCitizen : e.target.checked})

  }

  render(){
    const{isCitizen} = this.state
    return(
      <form>
        <h1>Are you a citizen? : {isCitizen ? "Yes" : "No"}</h1>
        <label>
          Are you a citizen?
          <input type="checkbox"
                 checked={this.isCitizen}
                 onChange={onChange}/>

        </label>
      </form>
    )
  }

}



export default App;
